/* ============================================
   Vision Dashboard â€” Dark Theme
   ============================================ */

:root {
  /* Colors */
  --bg-primary: #0d1117;
  --bg-secondary: #161b22;
  --bg-surface: #21262d;
  --bg-surface-hover: #30363d;
  --border: #30363d;
  --border-light: #21262d;

  --text-primary: #c9d1d9;
  --text-secondary: #8b949e;
  --text-heading: #f0f6fc;
  --text-muted: #6e7681;

  --accent: #58a6ff;
  --accent-hover: #79b8ff;
  --accent-subtle: rgba(88, 166, 255, 0.15);

  --success: #3fb950;
  --warning: #d29922;
  --danger: #f85149;

  /* Spacing */
  --space-xs: 4px;
  --space-sm: 8px;
  --space-md: 16px;
  --space-lg: 24px;
  --space-xl: 32px;

  /* Typography */
  --font-sans: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Noto Sans', Helvetica, Arial, sans-serif;
  --font-mono: 'JetBrains Mono', 'Fira Code', 'SF Mono', Consolas, monospace;

  /* Sizes */
  --sidebar-width: 280px;
  --header-height: 56px;
}

/* Reset */
*, *::before, *::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

html, body {
  height: 100%;
  overflow: hidden;
}

body {
  font-family: var(--font-sans);
  font-size: 14px;
  line-height: 1.5;
  color: var(--text-primary);
  background: linear-gradient(135deg, var(--bg-primary) 0%, var(--bg-secondary) 100%);
}

/* App Layout */
.app {
  display: flex;
  flex-direction: column;
  height: 100vh;
}

/* ============================================
   Header
   ============================================ */

.header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: var(--header-height);
  padding: 0 var(--space-lg);
  background: var(--bg-surface);
  border-bottom: 1px solid var(--border);
}

.header-left {
  display: flex;
  align-items: center;
  gap: var(--space-md);
}

.logo {
  font-size: 18px;
  font-weight: 600;
  color: var(--text-heading);
}

.header-right {
  display: flex;
  align-items: center;
  gap: var(--space-md);
}

/* Buttons */
.btn {
  display: inline-flex;
  align-items: center;
  gap: var(--space-sm);
  padding: var(--space-sm) var(--space-md);
  font-size: 13px;
  font-weight: 500;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  transition: all 0.15s ease;
}

.btn-primary {
  background: var(--accent);
  color: #fff;
}

.btn-primary:hover {
  background: var(--accent-hover);
}

.btn-secondary {
  background: var(--bg-surface-hover);
  color: var(--text-primary);
  border: 1px solid var(--border);
}

.btn-secondary:hover {
  background: var(--border);
}

.btn-ghost {
  background: transparent;
  color: var(--text-secondary);
  padding: var(--space-xs) var(--space-sm);
}

.btn-ghost:hover {
  color: var(--text-primary);
  background: var(--bg-surface-hover);
}

.btn-icon {
  font-size: 14px;
}

/* Dropdown */
.recent-dropdown {
  position: relative;
}

.dropdown-arrow {
  font-size: 10px;
  margin-left: var(--space-xs);
}

.dropdown-content {
  display: none;
  position: absolute;
  top: 100%;
  right: 0;
  margin-top: var(--space-xs);
  min-width: 280px;
  max-height: 400px;
  overflow-y: auto;
  background: var(--bg-surface);
  border: 1px solid var(--border);
  border-radius: 8px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.4);
  z-index: 100;
}

.dropdown-content.show {
  display: block;
}

.dropdown-item {
  display: block;
  width: 100%;
  padding: var(--space-sm) var(--space-md);
  text-align: left;
  font-size: 13px;
  color: var(--text-primary);
  background: none;
  border: none;
  cursor: pointer;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.dropdown-item:hover {
  background: var(--bg-surface-hover);
}

.dropdown-empty {
  padding: var(--space-md);
  color: var(--text-muted);
  font-style: italic;
  text-align: center;
}

/* ============================================
   Main Layout
   ============================================ */

.main {
  display: flex;
  flex: 1;
  overflow: hidden;
}

/* ============================================
   Sidebar
   ============================================ */

.sidebar {
  width: var(--sidebar-width);
  background: var(--bg-secondary);
  border-right: 1px solid var(--border);
  overflow-y: auto;
  flex-shrink: 0;
}

.sidebar-section {
  padding: var(--space-md);
}

.sidebar-title {
  font-size: 12px;
  font-weight: 600;
  color: var(--text-secondary);
  text-transform: uppercase;
  letter-spacing: 0.5px;
  margin-bottom: var(--space-md);
}

/* File Tree */
.file-tree {
  font-size: 13px;
}

.tree-item {
  display: flex;
  align-items: center;
  padding: var(--space-xs) var(--space-sm);
  border-radius: 4px;
  cursor: pointer;
  user-select: none;
}

.tree-item:hover {
  background: var(--bg-surface-hover);
}

.tree-item.active {
  background: var(--accent-subtle);
  color: var(--accent);
}

.tree-folder {
  font-weight: 500;
}

.tree-icon {
  margin-right: var(--space-sm);
  font-size: 12px;
  width: 16px;
  text-align: center;
}

.tree-children {
  margin-left: var(--space-md);
  display: none;
}

.tree-children.open {
  display: block;
}

/* ============================================
   Content Area
   ============================================ */

.content {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

/* Vision Panel */
.vision-panel {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.tabs {
  display: flex;
  gap: var(--space-xs);
  padding: var(--space-md) var(--space-lg);
  background: var(--bg-surface);
  border-bottom: 1px solid var(--border);
}

.tab {
  padding: var(--space-sm) var(--space-lg);
  font-size: 14px;
  font-weight: 500;
  color: var(--text-secondary);
  background: transparent;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  transition: all 0.15s ease;
}

.tab:hover {
  color: var(--text-primary);
  background: var(--bg-surface-hover);
}

.tab.active {
  color: var(--accent);
  background: var(--accent-subtle);
}

.vision-content {
  flex: 1;
  overflow-y: auto;
  padding: var(--space-lg) var(--space-xl);
}

/* ============================================
   Editor Panel
   ============================================ */

.editor-panel {
  display: flex;
  flex-direction: column;
  border-top: 1px solid var(--border);
  background: var(--bg-secondary);
  height: 50%;
  min-height: 200px;
}

.editor-panel.hidden {
  display: none;
}

.editor-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--space-sm) var(--space-md);
  background: var(--bg-surface);
  border-bottom: 1px solid var(--border);
}

.editor-path {
  font-size: 13px;
  color: var(--text-secondary);
  font-family: var(--font-mono);
}

.editor-actions {
  display: flex;
  align-items: center;
  gap: var(--space-md);
}

.save-status {
  font-size: 12px;
  color: var(--text-muted);
}

.save-status.saving {
  color: var(--warning);
}

.save-status.saved {
  color: var(--success);
}

.editor-split {
  display: flex;
  flex: 1;
  overflow: hidden;
}

.editor-preview {
  flex: 1;
  overflow-y: auto;
  padding: var(--space-lg);
  border-right: 1px solid var(--border);
}

.editor-input-container {
  flex: 1;
  display: flex;
  overflow: hidden;
}

.editor-input {
  flex: 1;
  padding: var(--space-lg);
  font-family: var(--font-mono);
  font-size: 13px;
  line-height: 1.6;
  color: var(--text-primary);
  background: var(--bg-primary);
  border: none;
  resize: none;
  outline: none;
}

.editor-input::placeholder {
  color: var(--text-muted);
}

/* ============================================
   Markdown Styling
   ============================================ */

.markdown-body {
  color: var(--text-primary);
  line-height: 1.7;
}

.markdown-body h1,
.markdown-body h2,
.markdown-body h3,
.markdown-body h4,
.markdown-body h5,
.markdown-body h6 {
  color: var(--text-heading);
  margin-top: 1.5em;
  margin-bottom: 0.5em;
  font-weight: 600;
  line-height: 1.3;
}

.markdown-body h1 {
  font-size: 2em;
  padding-bottom: 0.3em;
  border-bottom: 1px solid var(--border);
}

.markdown-body h2 {
  font-size: 1.5em;
  padding-bottom: 0.2em;
  border-bottom: 1px solid var(--border-light);
}

.markdown-body h3 {
  font-size: 1.25em;
}

.markdown-body p {
  margin-bottom: 1em;
}

.markdown-body a {
  color: var(--accent);
  text-decoration: none;
}

.markdown-body a:hover {
  text-decoration: underline;
}

.markdown-body strong {
  color: var(--text-heading);
  font-weight: 600;
}

.markdown-body em {
  font-style: italic;
}

.markdown-body blockquote {
  margin: 1em 0;
  padding: 0.5em 1em;
  border-left: 4px solid var(--accent);
  background: var(--bg-surface);
  border-radius: 0 4px 4px 0;
}

.markdown-body blockquote p {
  margin: 0;
}

.markdown-body ul,
.markdown-body ol {
  margin: 1em 0;
  padding-left: 2em;
}

.markdown-body li {
  margin: 0.25em 0;
}

.markdown-body code {
  font-family: var(--font-mono);
  font-size: 0.9em;
  padding: 0.2em 0.4em;
  background: var(--bg-surface);
  border-radius: 4px;
}

.markdown-body pre {
  margin: 1em 0;
  padding: 1em;
  background: var(--bg-surface);
  border-radius: 8px;
  overflow-x: auto;
}

.markdown-body pre code {
  padding: 0;
  background: transparent;
}

.markdown-body hr {
  margin: 2em 0;
  border: none;
  border-top: 1px solid var(--border);
}

.markdown-body table {
  width: 100%;
  margin: 1em 0;
  border-collapse: collapse;
}

.markdown-body th,
.markdown-body td {
  padding: var(--space-sm) var(--space-md);
  border: 1px solid var(--border);
  text-align: left;
}

.markdown-body th {
  background: var(--bg-surface);
  font-weight: 600;
  color: var(--text-heading);
}

.markdown-body tr:nth-child(even) {
  background: var(--bg-surface);
}

/* Callouts (Obsidian-style) */
.markdown-body .callout {
  margin: 1em 0;
  padding: 1em;
  border-radius: 8px;
  background: var(--bg-surface);
  border-left: 4px solid var(--accent);
}

.markdown-body .callout-title {
  font-weight: 600;
  margin-bottom: 0.5em;
  color: var(--text-heading);
}

/* Task lists */
.markdown-body input[type="checkbox"] {
  margin-right: var(--space-sm);
  accent-color: var(--accent);
}

/* ============================================
   Scrollbar
   ============================================ */

::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb {
  background: var(--border);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--bg-surface-hover);
}

/* ============================================
   Utilities
   ============================================ */

.hidden {
  display: none !important;
}

.truncate {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

