.file-tree {
  font-size: 13px;
}

.tree-item {
  display: flex;
  align-items: center;
  padding: var(--space-xs) var(--space-sm);
  border-radius: 4px;
  cursor: pointer;
  user-select: none;
}

.tree-item:hover {
  background: var(--bg-surface-hover);
}

.tree-item.active {
  background: var(--accent-subtle);
  color: var(--accent);
}

.tree-folder {
  font-weight: 500;
}

.tree-icon {
  margin-right: var(--space-sm);
  font-size: 12px;
  width: 16px;
  text-align: center;
}

.tree-children {
  margin-left: var(--space-md);
  display: none;
}

.tree-children.open {
  display: block;
}

/* ============================================
   Content Area
   ============================================ */

.content {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

/* Vision Panel */
.vision-panel {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.tabs {
  display: flex;
  gap: var(--space-xs);
  padding: var(--space-md) var(--space-lg);
  background: var(--bg-surface);
  border-bottom: 1px solid var(--border);
}

.tab {
  padding: var(--space-sm) var(--space-lg);
  font-size: 14px;
  font-weight: 500;
  color: var(--text-secondary);
  background: transparent;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  transition: all 0.15s ease;
}

.tab:hover {
  color: var(--text-primary);
  background: var(--bg-surface-hover);
}

.tab.active {
  color: var(--accent);
  background: var(--accent-subtle);
}

.vision-content {
  flex: 1;
  overflow-y: auto;
  padding: var(--space-lg) var(--space-xl);
}

/* ============================================
   Editor Panel
   ============================================ */

.editor-panel {
  display: flex;
  flex-direction: column;
  border-top: 1px solid var(--border);
  background: var(--bg-secondary);
  height: 50%;
  min-height: 200px;
}

.editor-panel.hidden {
  display: none;
}

.editor-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--space-sm) var(--space-md);
  background: var(--bg-surface);
  border-bottom: 1px solid var(--border);
}

.editor-path {
  font-size: 13px;
  color: var(--text-secondary);
  font-family: var(--font-mono);
}

.editor-actions {
  display: flex;
  align-items: center;
  gap: var(--space-md);
}

.save-status {
  font-size: 12px;
