  overflow: hidden;
}

.editor-preview {
  flex: 1;
  overflow-y: auto;
  padding: var(--space-lg);
  border-right: 1px solid var(--border);
}

.editor-input-container {
  flex: 1;
  display: flex;
  overflow: hidden;
}

.editor-input {
