  --bg-primary: #0d1117;
  --bg-secondary: #161b22;
  --bg-surface: #21262d;
  --bg-surface-hover: #30363d;
  --border: #30363d;
  --border-light: #21262d;

  --text-primary: #c9d1d9;
  --text-secondary: #8b949e;
  --text-heading: #f0f6fc;
  --text-muted: #6e7681;

  --accent: #58a6ff;
  --accent-hover: #79b8ff;
