.tree-item:hover {
  background: var(--bg-surface-hover);
}

.tree-item.active {
  background: var(--accent-subtle);
  color: var(--accent);
}

.tree-folder {
  font-weight: 500;
}

.tree-icon {
  margin-right: var(--space-sm);
  font-size: 12px;
  width: 16px;
  text-align: center;
}

.tree-children {
  margin-left: var(--space-md);
  display: none;
}

.tree-children.open {
