/* ============================================
   Vision Dashboard â€” Dark Theme
   ============================================ */

:root {
  /* Colors */
  --bg-primary: #0d1117;
  --bg-secondary: #161b22;
  --bg-surface: #21262d;
  --bg-surface-hover: #30363d;
  --border: #30363d;
  --border-light: #21262d;

  --text-primary: #c9d1d9;
  --text-secondary: #8b949e;
  --text-heading: #f0f6fc;
  --text-muted: #6e7681;

  --accent: #58a6ff;
  --accent-hover: #79b8ff;
  --accent-subtle: rgba(88, 166, 255, 0.15);

  --success: #3fb950;
  --warning: #d29922;
  --danger: #f85149;

  /* Spacing */
  --space-xs: 4px;
  --space-sm: 8px;
  --space-md: 16px;
  --space-lg: 24px;
  --space-xl: 32px;

  /* Typography */
  --font-sans: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Noto Sans', Helvetica, Arial, sans-serif;
  --font-mono: 'JetBrains Mono', 'Fira Code', 'SF Mono', Consolas, monospace;

  /* Sizes */
  --sidebar-width: 280px;
  --header-height: 56px;
}

/* Reset */
*, *::before, *::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

html, body {
  height: 100%;
  overflow: hidden;
}

body {
  font-family: var(--font-sans);
  font-size: 14px;
  line-height: 1.5;
  color: var(--text-primary);
  background: linear-gradient(135deg, var(--bg-primary) 0%, var(--bg-secondary) 100%);
}

/* App Layout */
.app {
  display: flex;
  flex-direction: column;
  height: 100vh;
}

/* ============================================
   Header
   ============================================ */

.header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: var(--header-height);
  padding: 0 var(--space-lg);
  background: var(--bg-surface);
  border-bottom: 1px solid var(--border);
}

.header-left {
  display: flex;
  align-items: center;
  gap: var(--space-md);
}

.logo {
  font-size: 18px;
  font-weight: 600;
  color: var(--text-heading);
}

.header-right {
  display: flex;
  align-items: center;
  gap: var(--space-md);
}

/* Buttons */
.btn {
  display: inline-flex;
  align-items: center;
  gap: var(--space-sm);
  padding: var(--space-sm) var(--space-md);
  font-size: 13px;
  font-weight: 500;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  transition: all 0.15s ease;
}

.btn-primary {
  background: var(--accent);
  color: #fff;
}

.btn-primary:hover {
  background: var(--accent-hover);
}

.btn-secondary {
  background: var(--bg-surface-hover);
  color: var(--text-primary);
  border: 1px solid var(--border);
}

.btn-secondary:hover {
  background: var(--border);
}

.btn-ghost {
  background: transparent;
  color: var(--text-secondary);
  padding: var(--space-xs) var(--space-sm);
}

.btn-ghost:hover {
  color: var(--text-primary);
  background: var(--bg-surface-hover);
}

.btn-sm {
  padding: var(--space-xs) var(--space-sm);
  font-size: 12px;
}

.btn-icon {
  font-size: 14px;
}

.logo-btn {
  font-size: 18px;
  font-weight: 600;
  color: var(--text-heading);
  padding: var(--space-sm) var(--space-md);
}

.logo-btn:hover {
  color: var(--accent);
}

.header-center {
  flex: 1;
  text-align: center;
}

.current-view {
  font-size: 13px;
  color: var(--text-secondary);
  font-family: var(--font-mono);
}

/* Dropdown */
.recent-dropdown {
  position: relative;
}

.dropdown-arrow {
  font-size: 10px;
  margin-left: var(--space-xs);
}

.dropdown-content {
  display: none;
  position: absolute;
  top: 100%;
  right: 0;
  margin-top: var(--space-xs);
  min-width: 280px;
  max-height: 400px;
  overflow-y: auto;
  background: var(--bg-surface);
  border: 1px solid var(--border);
  border-radius: 8px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.4);
  z-index: 100;
}

.dropdown-content.show {
  display: block;
}

.dropdown-item {
  display: block;
  width: 100%;
  padding: var(--space-sm) var(--space-md);
  text-align: left;
  font-size: 13px;
  color: var(--text-primary);
  background: none;
  border: none;
  cursor: pointer;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.dropdown-item:hover {
  background: var(--bg-surface-hover);
}

.dropdown-empty {
  padding: var(--space-md);
  color: var(--text-muted);
  font-style: italic;
  text-align: center;
}

/* ============================================
   Main Layout
   ============================================ */

.main {
  display: flex;
  flex: 1;
  overflow: hidden;
}

/* ============================================
   Sidebar
   ============================================ */

.sidebar {
  width: var(--sidebar-width);
  background: var(--bg-secondary);
  border-right: 1px solid var(--border);
  display: flex;
  flex-direction: column;
  flex-shrink: 0;
}

.sidebar-section {
  padding: var(--space-md);
}

.sidebar-files {
  flex: 1;
  overflow-y: auto;
  border-bottom: 1px solid var(--border);
}

.sidebar-recent {
  flex-shrink: 0;
  max-height: 280px;
  overflow-y: auto;
}

.recent-list {
  font-size: 13px;
}

.recent-item {
  display: block;
  width: 100%;
  padding: var(--space-xs) var(--space-sm);
  text-align: left;
  font-size: 13px;
  color: var(--text-primary);
  background: none;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.recent-item:hover {
  background: var(--bg-surface-hover);
}

.recent-empty {
  padding: var(--space-sm);
  color: var(--text-muted);
  font-style: italic;
  font-size: 12px;
}

.sidebar-title {
  font-size: 12px;
  font-weight: 600;
  color: var(--text-secondary);
  text-transform: uppercase;
  letter-spacing: 0.5px;
  margin-bottom: var(--space-md);
}

/* File Tree */
.file-tree {
  font-size: 13px;
}

.tree-item {
  display: flex;
  align-items: center;
  padding: var(--space-xs) var(--space-sm);
  border-radius: 4px;
  cursor: pointer;
  user-select: none;
}

.tree-item:hover {
  background: var(--bg-surface-hover);
}

.tree-item.active {
  background: var(--accent-subtle);
  color: var(--accent);
}

.tree-folder {
  font-weight: 500;
}

.tree-icon {
  margin-right: var(--space-sm);
  font-size: 12px;
  width: 16px;
  text-align: center;
}

.tree-children {
  margin-left: var(--space-md);
  display: none;
}

.tree-children.open {
  display: block;
}

/* ============================================
   Content Area
   ============================================ */

.content {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

/* Vision Panel */
.vision-panel {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.tabs {
  display: flex;
  gap: var(--space-xs);
  padding: var(--space-md) var(--space-lg);
  background: var(--bg-surface);
  border-bottom: 1px solid var(--border);
}

.tab {
  padding: var(--space-sm) var(--space-lg);
  font-size: 14px;
  font-weight: 500;
  color: var(--text-secondary);
  background: transparent;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  transition: all 0.15s ease;
}

.tab:hover {
  color: var(--text-primary);
  background: var(--bg-surface-hover);
}

.tab.active {
  color: var(--accent);
  background: var(--accent-subtle);
}

.vision-content {
  flex: 1;
  overflow-y: auto;
  padding: var(--space-lg) var(--space-xl);
}

/* ============================================
   Editor Panel
   ============================================ */

.editor-panel {
  display: flex;
  flex-direction: column;
  background: var(--bg-secondary);
  flex: 1;
  overflow: hidden;
}

.editor-panel.hidden {
  display: none;
}

.editor-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--space-sm) var(--space-md);
  background: var(--bg-surface);
  border-bottom: 1px solid var(--border);
}

.editor-path {
  font-size: 13px;
  color: var(--text-secondary);
  font-family: var(--font-mono);
}

.editor-actions {
  display: flex;
  align-items: center;
  gap: var(--space-md);
}

.save-status {
  font-size: 12px;
  color: var(--text-muted);
}

.save-status.saving {
  color: var(--warning);
}

.save-status.saved {
  color: var(--success);
}

/* Editor Toolbar */
.editor-toolbar {
  display: flex;
  align-items: center;
  gap: var(--space-sm);
  padding: var(--space-sm) var(--space-md);
  background: var(--bg-surface);
  border-bottom: 1px solid var(--border);
}

.toolbar-group {
  display: flex;
  gap: 2px;
}

.toolbar-divider {
  width: 1px;
  height: 24px;
  background: var(--border);
  margin: 0 var(--space-sm);
}

.toolbar-btn {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  font-size: 13px;
  color: var(--text-secondary);
  background: transparent;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  transition: all 0.15s ease;
}

.toolbar-btn:hover {
  color: var(--text-primary);
  background: var(--bg-surface-hover);
}

.toolbar-btn.active {
  color: var(--accent);
  background: var(--accent-subtle);
}

/* Editor Content Area */
.editor-content {
  flex: 1;
  overflow: hidden;
  display: flex;
}

.tiptap-editor {
  flex: 1;
  overflow-y: auto;
  padding: var(--space-lg) var(--space-xl);
  background: var(--bg-primary);
}

.source-editor {
  flex: 1;
  padding: var(--space-lg) var(--space-xl);
  font-family: var(--font-mono);
  font-size: 13px;
  line-height: 1.6;
  color: var(--text-primary);
  background: var(--bg-primary);
  border: none;
  resize: none;
  outline: none;
}

.source-editor::placeholder {
  color: var(--text-muted);
}

/* Tiptap Editor Styling */
.tiptap-editor .ProseMirror {
  outline: none;
  min-height: 100%;
}

.tiptap-editor .ProseMirror p.is-editor-empty:first-child::before {
  content: attr(data-placeholder);
  float: left;
  color: var(--text-muted);
  pointer-events: none;
  height: 0;
}

.tiptap-editor .ProseMirror > * + * {
  margin-top: 0.75em;
}

.tiptap-editor h1,
.tiptap-editor h2,
.tiptap-editor h3,
.tiptap-editor h4 {
  color: var(--text-heading);
  font-weight: 600;
  line-height: 1.3;
  margin-top: 1.5em;
  margin-bottom: 0.5em;
}

.tiptap-editor h1 {
  font-size: 2em;
  border-bottom: 1px solid var(--border);
  padding-bottom: 0.3em;
}

.tiptap-editor h2 {
  font-size: 1.5em;
  border-bottom: 1px solid var(--border-light);
  padding-bottom: 0.2em;
}

.tiptap-editor h3 {
  font-size: 1.25em;
}

.tiptap-editor p {
  margin: 0;
}

.tiptap-editor strong {
  color: var(--text-heading);
  font-weight: 600;
}

.tiptap-editor em {
  font-style: italic;
}

.tiptap-editor s {
  text-decoration: line-through;
}

.tiptap-editor a {
  color: var(--accent);
  text-decoration: none;
}

.tiptap-editor a:hover {
  text-decoration: underline;
}

.tiptap-editor code {
  font-family: var(--font-mono);
  font-size: 0.9em;
  padding: 0.2em 0.4em;
  background: var(--bg-surface);
  border-radius: 4px;
}

.tiptap-editor pre {
  margin: 1em 0;
  padding: 1em;
  background: var(--bg-surface);
  border-radius: 8px;
  overflow-x: auto;
}

.tiptap-editor pre code {
  padding: 0;
  background: transparent;
}

.tiptap-editor blockquote {
  margin: 1em 0;
  padding: 0.5em 1em;
  border-left: 4px solid var(--accent);
  background: var(--bg-surface);
  border-radius: 0 4px 4px 0;
}

.tiptap-editor ul,
.tiptap-editor ol {
  margin: 0.5em 0;
  padding-left: 1.5em;
}

.tiptap-editor li {
  margin: 0.25em 0;
}

.tiptap-editor li p {
  margin: 0;
}

/* Task list styling */
.tiptap-editor ul[data-type="taskList"] {
  list-style: none;
  padding-left: 0;
}

.tiptap-editor ul[data-type="taskList"] li {
  display: flex;
  align-items: flex-start;
  gap: var(--space-sm);
}

.tiptap-editor ul[data-type="taskList"] li > label {
  flex-shrink: 0;
  margin-top: 0.25em;
}

.tiptap-editor ul[data-type="taskList"] li > div {
  flex: 1;
}

.tiptap-editor ul[data-type="taskList"] input[type="checkbox"] {
  accent-color: var(--accent);
  width: 16px;
  height: 16px;
  cursor: pointer;
}

.tiptap-editor hr {
  margin: 2em 0;
  border: none;
  border-top: 1px solid var(--border);
}

/* ============================================
   Markdown Styling
   ============================================ */

.markdown-body {
  color: var(--text-primary);
  line-height: 1.7;
}

.markdown-body h1,
.markdown-body h2,
.markdown-body h3,
.markdown-body h4,
.markdown-body h5,
.markdown-body h6 {
  color: var(--text-heading);
  margin-top: 1.5em;
  margin-bottom: 0.5em;
  font-weight: 600;
  line-height: 1.3;
}

.markdown-body h1 {
  font-size: 2em;
  padding-bottom: 0.3em;
  border-bottom: 1px solid var(--border);
}

.markdown-body h2 {
  font-size: 1.5em;
  padding-bottom: 0.2em;
