  border-radius: 4px;
  cursor: pointer;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.recent-item:hover {
  background: var(--bg-surface-hover);
}

.recent-empty {
  padding: var(--space-sm);
  color: var(--text-muted);
  font-style: italic;
  font-size: 12px;
}

.sidebar-title {
  font-size: 12px;
  font-weight: 600;
  color: var(--text-secondary);
  text-transform: uppercase;
  letter-spacing: 0.5px;
  margin-bottom: var(--space-md);
}

/* File Tree */
.file-tree {
  font-size: 13px;
}

.tree-item {
  display: flex;
  align-items: center;
  padding: var(--space-xs) var(--space-sm);
  border-radius: 4px;
  cursor: pointer;
  user-select: none;
}

.tree-item:hover {
  background: var(--bg-surface-hover);
}

.tree-item.active {
  background: var(--accent-subtle);
  color: var(--accent);
}

.tree-folder {
  font-weight: 500;
}

.tree-icon {
  margin-right: var(--space-sm);
  font-size: 12px;
  width: 16px;
  text-align: center;
}

.tree-children {
  margin-left: var(--space-md);
  display: none;
}

.tree-children.open {
  display: block;
}

/* ============================================
   Content Area
   ============================================ */

.content {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

/* Vision Panel */
.vision-panel {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.tabs {
  display: flex;
  gap: var(--space-xs);
  padding: var(--space-md) var(--space-lg);
  background: var(--bg-surface);
  border-bottom: 1px solid var(--border);
}

.tab {
  padding: var(--space-sm) var(--space-lg);
  font-size: 14px;
