// ============================================
// Vision Dashboard â€” Frontend App with Tiptap
// ============================================

// State
let currentFile = null;
let saveTimeout = null;
let recentFiles = [];
let editor = null;
let isSourceView = false;
let turndownService = null;

// DOM Elements
const fileTree = document.getElementById('fileTree');
const visionPanel = document.getElementById('visionPanel');
const visionContent = document.getElementById('visionContent');
const editorPanel = document.getElementById('editorPanel');
const editorPath = document.getElementById('editorPath');
const tiptapEditor = document.getElementById('tiptapEditor');
const sourceEditor = document.getElementById('sourceEditor');
const saveStatus = document.getElementById('saveStatus');
const currentView = document.getElementById('currentView');
const todayBtn = document.getElementById('todayBtn');
const visionBtn = document.getElementById('visionBtn');
const recentList = document.getElementById('recentList');
const closeEditor = document.getElementById('closeEditor');
const toggleSource = document.getElementById('toggleSource');
const tabs = document.querySelectorAll('.tab');
const toolbarBtns = document.querySelectorAll('.toolbar-btn');

// ============================================
// Initialize
// ============================================

document.addEventListener('DOMContentLoaded', () => {
  // Initialize Turndown (HTML to Markdown converter)
  turndownService = new TurndownService({
    headingStyle: 'atx',
    bulletListMarker: '-',
    codeBlockStyle: 'fenced'
  });

  // Custom rules for task lists
  turndownService.addRule('taskListItem', {
    filter: (node) => {
      return node.nodeName === 'LI' &&
             node.parentNode.getAttribute('data-type') === 'taskList';
    },
    replacement: (content, node) => {
      const checkbox = node.querySelector('input[type="checkbox"]');
      const checked = checkbox && checkbox.checked;
      return `- [${checked ? 'x' : ' '}] ${content.trim()}\n`;
    }
  });

  // Initialize Tiptap
  initTiptap();

  // Load data
  loadFileTree();
  loadVision('karthik');
  loadRecent();

  // Tab switching
  tabs.forEach(tab => {
    tab.addEventListener('click', () => {
      tabs.forEach(t => t.classList.remove('active'));
      tab.classList.add('active');
      loadVision(tab.dataset.tab);
    });
  });

  // Today's note button
  todayBtn.addEventListener('click', openTodaysNote);

  // Vision button (logo) - go back to vision view
  visionBtn.addEventListener('click', showVisionPanel);

  // Close editor button
  closeEditor.addEventListener('click', showVisionPanel);

  // Toggle source view
  toggleSource.addEventListener('click', toggleSourceView);

  // Source editor input - auto-save
  sourceEditor.addEventListener('input', () => {
    scheduleSave();
  });

  // Toolbar buttons
  toolbarBtns.forEach(btn => {
    btn.addEventListener('click', () => {
      handleToolbarAction(btn.dataset.action);
    });
  });

  // Keyboard shortcuts
  document.addEventListener('keydown', (e) => {
    // Cmd/Ctrl + S to save
    if ((e.metaKey || e.ctrlKey) && e.key === 's') {
      e.preventDefault();
      if (currentFile) {
        saveFile();
      }
    }
    // Escape to close editor and go back to vision
    if (e.key === 'Escape') {
      showVisionPanel();
    }
  });
});

// ============================================
// Tiptap Initialization
// ============================================

function initTiptap() {
  const { Editor } = window['@tiptap/core'];
  const StarterKit = window['@tiptap/starter-kit'].StarterKit;
  const Placeholder = window['@tiptap/extension-placeholder'].Placeholder;
  const TaskList = window['@tiptap/extension-task-list'].TaskList;
  const TaskItem = window['@tiptap/extension-task-item'].TaskItem;

  editor = new Editor({
    element: tiptapEditor,
    extensions: [
      StarterKit,
      Placeholder.configure({
        placeholder: 'Start writing...'
      }),
      TaskList,
      TaskItem.configure({
        nested: true
      })
    ],
    content: '',
    onUpdate: () => {
      if (!isSourceView) {
        scheduleSave();
        updateToolbarState();
      }
    },
    onSelectionUpdate: () => {
      updateToolbarState();
    }
  });
}

function updateToolbarState() {
  if (!editor) return;

  toolbarBtns.forEach(btn => {
    const action = btn.dataset.action;
    let isActive = false;

    switch (action) {
      case 'bold':
        isActive = editor.isActive('bold');
        break;
      case 'italic':
        isActive = editor.isActive('italic');
        break;
      case 'strike':
        isActive = editor.isActive('strike');
        break;
      case 'code':
        isActive = editor.isActive('code');
        break;
      case 'h1':
        isActive = editor.isActive('heading', { level: 1 });
        break;
      case 'h2':
        isActive = editor.isActive('heading', { level: 2 });
        break;
      case 'h3':
        isActive = editor.isActive('heading', { level: 3 });
        break;
      case 'bulletList':
        isActive = editor.isActive('bulletList');
        break;
      case 'orderedList':
        isActive = editor.isActive('orderedList');
        break;
      case 'taskList':
        isActive = editor.isActive('taskList');
        break;
      case 'blockquote':
        isActive = editor.isActive('blockquote');
        break;
      case 'codeBlock':
        isActive = editor.isActive('codeBlock');
        break;
    }

    btn.classList.toggle('active', isActive);
  });
}

function handleToolbarAction(action) {
  if (!editor) return;

  switch (action) {
    case 'bold':
      editor.chain().focus().toggleBold().run();
      break;
    case 'italic':
      editor.chain().focus().toggleItalic().run();
      break;
    case 'strike':
      editor.chain().focus().toggleStrike().run();
      break;
    case 'code':
      editor.chain().focus().toggleCode().run();
      break;
    case 'h1':
      editor.chain().focus().toggleHeading({ level: 1 }).run();
      break;
    case 'h2':
      editor.chain().focus().toggleHeading({ level: 2 }).run();
      break;
    case 'h3':
      editor.chain().focus().toggleHeading({ level: 3 }).run();
      break;
    case 'bulletList':
      editor.chain().focus().toggleBulletList().run();
      break;
    case 'orderedList':
      editor.chain().focus().toggleOrderedList().run();
      break;
    case 'taskList':
      editor.chain().focus().toggleTaskList().run();
      break;
    case 'blockquote':
      editor.chain().focus().toggleBlockquote().run();
      break;
    case 'codeBlock':
      editor.chain().focus().toggleCodeBlock().run();
      break;
    case 'horizontalRule':
      editor.chain().focus().setHorizontalRule().run();
      break;
  }
}

// ============================================
// Source View Toggle
// ============================================

function toggleSourceView() {
  isSourceView = !isSourceView;

  if (isSourceView) {
    // Switch to source view
    const markdown = getMarkdownFromEditor();
    sourceEditor.value = markdown;
    tiptapEditor.classList.add('hidden');
    sourceEditor.classList.remove('hidden');
    toggleSource.classList.add('active');
  } else {
    // Switch back to WYSIWYG
    const markdown = sourceEditor.value;
    setEditorContent(markdown);
    sourceEditor.classList.add('hidden');
    tiptapEditor.classList.remove('hidden');
    toggleSource.classList.remove('active');
  }
}

function getMarkdownFromEditor() {
  if (!editor) return '';

  const html = editor.getHTML();
  let markdown = turndownService.turndown(html);

  // Clean up extra newlines
  markdown = markdown.replace(/\n{3,}/g, '\n\n');

  return markdown;
}

function setEditorContent(markdown) {
  if (!editor) return;

  // Convert markdown to HTML
  const html = marked.parse(markdown);
  editor.commands.setContent(html);
}

// ============================================
// View Management
// ============================================

function showVisionPanel() {
  // Save before switching if there's content
  if (currentFile) {
    saveFile();
  }

  visionPanel.classList.remove('hidden');
  editorPanel.classList.add('hidden');
  currentFile = null;
  currentView.textContent = '';

  // Reset source view
  if (isSourceView) {
    isSourceView = false;
    sourceEditor.classList.add('hidden');
    tiptapEditor.classList.remove('hidden');
    toggleSource.classList.remove('active');
  }

  // Remove active state from tree
  document.querySelectorAll('.tree-file').forEach(f => f.classList.remove('active'));
}

function showEditorPanel(filePath) {
  visionPanel.classList.add('hidden');
  editorPanel.classList.remove('hidden');
  currentView.textContent = filePath;
}

// ============================================
// Vision Documents
// ============================================

async function loadVision(person) {
  try {
    const res = await fetch(`/api/vision/${person}`);
    const data = await res.json();

    if (data.error) {
      visionContent.innerHTML = `<p class="error">${data.error}</p>`;
      return;
    }

    // Strip HTML tags and render as markdown
    const cleanContent = stripHtml(data.content);
    visionContent.innerHTML = `<div class="markdown-body">${renderMarkdown(cleanContent)}</div>`;
  } catch (err) {
    visionContent.innerHTML = `<p class="error">Failed to load vision document</p>`;
  }
}

function stripHtml(content) {
  // Remove HTML div tags and style attributes but keep content
  return content
    .replace(/<div[^>]*>/gi, '')
    .replace(/<\/div>/gi, '')
    .replace(/style="[^"]*"/gi, '');
}

// ============================================
// File Tree
// ============================================

async function loadFileTree() {
  try {
    const res = await fetch('/api/files');
    const files = await res.json();
    fileTree.innerHTML = renderTree(files);
    attachTreeListeners();
  } catch (err) {
    fileTree.innerHTML = '<p class="error">Failed to load files</p>';
  }
}

function renderTree(items, level = 0) {
  let html = '';

  for (const item of items) {
    if (item.type === 'folder') {
      html += `
        <div class="tree-node" data-path="${item.path}">
          <div class="tree-item tree-folder" style="padding-left: ${level * 12}px">
            <span class="tree-icon">â–¶</span>
            <span class="tree-name">${item.name}</span>
          </div>
          <div class="tree-children">
            ${renderTree(item.children, level + 1)}
          </div>
        </div>
      `;
    } else {
      html += `
        <div class="tree-item tree-file" data-path="${item.path}" style="padding-left: ${(level * 12) + 16}px">
          <span class="tree-icon">ğŸ“„</span>
          <span class="tree-name">${item.name.replace('.md', '')}</span>
        </div>
      `;
    }
  }

  return html;
}

function attachTreeListeners() {
  // Folder toggle
  document.querySelectorAll('.tree-folder').forEach(folder => {
    folder.addEventListener('click', (e) => {
      const node = folder.closest('.tree-node');
      const children = node.querySelector('.tree-children');
      const icon = folder.querySelector('.tree-icon');

      if (children.classList.contains('open')) {
        children.classList.remove('open');
        icon.textContent = 'â–¶';
      } else {
        children.classList.add('open');
        icon.textContent = 'â–¼';
      }
    });
  });

  // File click
  document.querySelectorAll('.tree-file').forEach(file => {
    file.addEventListener('click', () => {
      const path = file.dataset.path;
      openFile(path);

      // Update active state
      document.querySelectorAll('.tree-file').forEach(f => f.classList.remove('active'));
      file.classList.add('active');
    });
  });
}

// ============================================
// File Operations
// ============================================

async function openFile(filePath) {
  try {
    const res = await fetch(`/api/file?path=${encodeURIComponent(filePath)}`);
    const data = await res.json();

    if (data.error) {
      alert(data.error);
      return;
    }

    currentFile = filePath;
    editorPath.textContent = filePath;

    // Load content into editor
    setEditorContent(data.content);

    // Also set source editor
    sourceEditor.value = data.content;

    showEditorPanel(filePath);
    saveStatus.textContent = '';

    // Refresh recent list
    loadRecent();
  } catch (err) {
    alert('Failed to open file');
  }
}

async function saveFile() {
  if (!currentFile) return;

  saveStatus.textContent = 'Saving...';
  saveStatus.className = 'save-status saving';

  // Get content from appropriate source
  let content;
  if (isSourceView) {
    content = sourceEditor.value;
  } else {
    content = getMarkdownFromEditor();
  }

  try {
    const res = await fetch('/api/file', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        path: currentFile,
        content: content
      })
    });

    const data = await res.json();

    if (data.success) {
      saveStatus.textContent = 'Saved';
      saveStatus.className = 'save-status saved';
      setTimeout(() => {
        saveStatus.textContent = '';
      }, 2000);
    } else {
      saveStatus.textContent = 'Save failed';
      saveStatus.className = 'save-status';
    }
  } catch (err) {
    saveStatus.textContent = 'Save failed';
    saveStatus.className = 'save-status';
  }
}

function scheduleSave() {
  if (saveTimeout) {
    clearTimeout(saveTimeout);
  }
  saveStatus.textContent = 'Typing...';
  saveStatus.className = 'save-status';

  saveTimeout = setTimeout(() => {
    saveFile();
  }, 1000);
}

// ============================================
// Today's Note
// ============================================

async function openTodaysNote() {
  try {
    const res = await fetch('/api/daily');
    const data = await res.json();

    if (data.error) {
      alert(data.error);
      return;
    }

    currentFile = data.path;
    editorPath.textContent = data.path;

    // Load content into editor
    setEditorContent(data.content);
    sourceEditor.value = data.content;

    showEditorPanel(data.path);

    if (data.created) {
      saveStatus.textContent = 'Created new note';
      saveStatus.className = 'save-status saved';
    } else {
      saveStatus.textContent = '';
    }

    // Refresh file tree to show new file
    if (data.created) {
      loadFileTree();
    }

    // Refresh recent
    loadRecent();
  } catch (err) {
    alert('Failed to open today\'s note');
  }
}

// ============================================
// Recent Files (Sidebar)
// ============================================

async function loadRecent() {
  try {
    const res = await fetch('/api/recent');
    recentFiles = await res.json();
    renderRecent();
  } catch (err) {
    console.error('Failed to load recent files');
  }
}

function renderRecent() {
  if (recentFiles.length === 0) {
    recentList.innerHTML = '<div class="recent-empty">No recent files</div>';
    return;
  }

  recentList.innerHTML = recentFiles.map(path => `
    <button class="recent-item" data-path="${path}" title="${path}">
      ğŸ“„ ${path.split('/').pop().replace('.md', '')}
    </button>
  `).join('');

  // Attach click listeners
  recentList.querySelectorAll('.recent-item').forEach(item => {
    item.addEventListener('click', () => {
      openFile(item.dataset.path);
    });
  });
}

// ============================================
// Markdown Rendering (for Vision docs)
// ============================================

function renderMarkdown(content) {
  // Configure marked
  marked.setOptions({
    breaks: true,
    gfm: true
  });

  // Pre-process: Convert Obsidian wiki-links to regular links
  let processed = content
    // [[link|text]] -> [text](#)
    .replace(/\[\[([^\]|]+)\|([^\]]+)\]\]/g, '[$2](#)')
    // [[link]] -> [link](#)
    .replace(/\[\[([^\]]+)\]\]/g, '[$1](#)');

  // Convert Obsidian callouts to HTML
  processed = processed.replace(
    /> \[!(\w+)\](-?)\s*([^\n]*)\n((?:>.*\n?)*)/g,
    (match, type, collapsed, title, content) => {
      const calloutContent = content.replace(/^> ?/gm, '').trim();
      return `<div class="callout callout-${type}">
        <div class="callout-title">${title || type}</div>
        <div class="callout-content">${marked.parse(calloutContent)}</div>
      </div>`;
    }
  );

  // Handle checkbox lists
  processed = processed
    .replace(/- \[ \]/g, '- <input type="checkbox" disabled>')
    .replace(/- \[x\]/gi, '- <input type="checkbox" checked disabled>');

  return marked.parse(processed);
}

